INSERT INTO films (id, genres, country, releaserus, title, year, director, authors, actors, release, duration, language, budget, age, pic, src, description, isseries, needspayment, slug) VALUES ('03f08ba6-5b84-44b8-bc2d-c054dc33d121', '{мелодрама,приключения,комедия,музыка,семейный}', 'США', '2003-01-01 03:00:00+03', 'Лиззи Магуайр', 2003, '{"Джим Фолл"}', '{"Сьюзэн Эстель Джэнсен","Эд Дектер","Джон Дж. Штраусс","Терри Мински"}', '{8dbbe32c-20e8-4616-9e7d-58049d4eb54c,c317e3e8-bc29-4fcb-aac4-658e9963f926,c3dd9a00-792a-45b6-a769-78c95fa1c028,9a00a6b9-5e47-49ff-a905-e852afe2ed4f,068e0383-2008-4d9c-bae3-30af5e5fcd15,f9a2acd5-4449-470c-935d-2b09283b2582}', '2003-01-01', 94, 'Ru', '???', 12, '{lizzi-maguair.png}', '{lizzi-maguair.mp4}', 'Тринадцатилетняя школьница Лиззи Магуайер и ее приятели Гордо, Кейт и Эсан собираются оттянуться по полной программе во время их поездки с классом в Италию.
INSERT INTO films (id, genres, country, releaserus, title, year, director, authors, actors, release, duration, language, budget, age, pic, src, description, isseries, needspayment, slug) VALUES ('5302ff80-90c8-4624-ac6d-9aad9ef9304b', '{фантастика,боевик}', 'США', '1999-01-01 03:00:00+03', 'Матрица', 1999, '{"Лана Вачовски","Лилли Вачовски"}', '{"Лилли Вачовски","Лана Вачовски"}', '{9bb99e8c-58c5-4a1d-9525-d7ab9bdca500,eb06287b-1644-48ee-b58c-818b41e9ac9a,4da6c414-1253-4eca-b767-bf00dc2ae364,1798520c-ae91-46f7-8b4b-c2e21bdd4774,b4e5e55d-9206-448e-adbb-f9462e7caf9e,e9c6ee20-33e8-4de5-b895-16b1878c4cba}', '1999-01-01', 136, 'Ru', '???', 16, '{matritsa.png}', '{matritsa.mp4}', 'Жизнь Томаса Андерсона разделена на две части: днём он — самый обычный офисный работник, получающий нагоняи от начальства, а ночью превращается в хакера по имени Нео, и нет места в сети, куда он бы не смог проникнуть. Но однажды всё меняется. Томас узнаёт ужасающую правду о реальности.', false, false, 'matritsa');
INSERT INTO films (id, genres, country, releaserus, title, year, director, authors, actors, release, duration, language, budget, age, pic, src, description, isseries, needspayment, slug) VALUES ('a908c2ba-e7b6-44e6-8239-608ea9b7f50f', '{драма,детектив,приключения,комедия,семейный}', 'США', '2003-01-01 03:00:00+03', 'Клад', 2003, '{"Эндрю Дэвис"}', '{"Луис Сачар"}', '{2baefab1-4158-4f2c-ae56-46deda17cc5a,a5fc8f43-783f-4280-9689-7c8ac11310bd,80dd2aee-31a5-451b-928a-e4761236ae43,519be7fb-46e6-41b8-9b4c-f5590280004e,33f81c9b-6e0c-494f-ab80-2926ca136496,75fa3615-2fbb-4c69-8332-bc57477c7e26}', '2003-01-01', 117, 'Ru', '???', 12, '{klad.png}', '{klad.mp4}', 'Стэнли арестован по ложному обвинению в краже кроссовок и отправлен в трудовой лагерь, расположенный в техасской пустыне. Воспитатели «закаляют характер» подростков странным наказанием. Ребята копают ямы в иссушенной земле, но не знают, что их на самом деле используют для раскопок таинственного клада. Однако Стэнли удается раскрыть загадочную связь между сокровищами и проклятием, тяготеющим долгие годы над его семьей…', false, false, 'klad');
INSERT INTO films (id, genres, country, releaserus, title, year, director, authors, actors, release, duration, language, budget, age, pic, src, description, isseries, needspayment, slug) VALUES ('73720352-8eab-42e9-b805-0465af86ab5d', '{мелодрама,комедия}', 'США', '2003-01-01 03:00:00+03', 'Управление гневом', 2003, '{"Питер Сигал"}', '{"Дэвид Дорфман"}', '{4663452a-5bf0-4d72-97a4-b6a78c49db88,77bb227c-ee74-47db-91df-3ec11e93579f,3d6ec5a7-afc9-436e-a54c-8d1043961cc1,6bfea0f2-eb6b-4dbf-9888-c89681076820,91a91050-8e47-4c8b-8629-a5ce796c7b7b,239884d5-8d9f-4077-af06-2f08a74e08a3}', '2003-01-01', 106, 'Ru', '???', 12, '{upravlenie-gnevom.png}', '{upravlenie-gnevom.mp4}', 'Скромному клерку отчаянно не везет. Парня по обвинению в нападении на бортпроводницу приговаривают к лечению у психиатра. Но верно говорят, что большинство психиатров сами немного безумны. Или сильно не в себе...', false, true, 'upravlenie-gnevom');
INSERT INTO films (id, genres, country, releaserus, title, year, director, authors, actors, release, duration, language, budget, age, pic, src, description, isseries, needspayment, slug) VALUES ('d73d5787-f5b7-451e-b62d-035abcb83376', '{триллер,криминал}', 'США', '2003-01-01 03:00:00+03', 'Афера', 2003, '{"Джеймс Фоули"}', '{"Даг Джанг"}', '{00aec877-c571-4fa8-a734-bb2336c63c55,b09da613-3083-496f-a728-590248f70582,def8db5e-866b-4f7b-bf5d-f2ae60db8288,77a08caf-1d5e-4e97-83ca-af969a3042df,a53a913e-3064-40cf-bc28-c097843b35ee,05a1708f-6009-4185-8a6f-b76672929400}', '2003-01-01', 97, 'Ru', '???', 16, '{afera.png}', '{afera.mp4}', 'Джейк Вига – хитроумный и обаятельный мошенник. Последняя афера Джейка привела к тому, что его дорожки пересеклись с мафией – при помощи своей команды он лишил нескольких тысяч долларов Лайонела Долби, – счетовода эксцентричного мафиозного босса Уинстона Кинга по прозвищу «Король».
INSERT INTO films (id, genres, country, releaserus, title, year, director, authors, actors, release, duration, language, budget, age, pic, src, description, isseries, needspayment, slug) VALUES ('3209cedf-216f-4426-99b8-e56a37778dba', '{фантастика,боевик}', 'США', '2003-01-01 03:00:00+03', 'Матрица: Революция', 2003, '{"Лана Вачовски","Лилли Вачовски"}', '{"Лилли Вачовски","Лана Вачовски"}', '{2a8ce30d-3533-46c4-8ede-752469b0ce07,3318bf28-3611-4f20-bed4-422ef25248b7,df81ab88-f53e-4cca-9335-25d654074b62,10a8de69-59a5-4d62-a397-3bedadc75ce9,84cbe340-df1a-482f-a528-aefbafba27ca,0fe424c3-80db-4626-a3a8-324e68123243}', '2003-01-01', 129, 'Ru', '???', 16, '{matritsa:-revoliutsiia.png}', '{matritsa:-revoliutsiia.mp4}', 'Пока армия Машин пытается уничтожить Зион, его жители из последних сил держат оборону. Но удастся ли им предотвратить полное вторжение в город кишащей орды беспощадных машин до того, как Нео соберет все свои силы и положит конец войне?', false, false, 'matritsa:-revoliutsiia');
INSERT INTO films (id, genres, country, releaserus, title, year, director, authors, actors, release, duration, language, budget, age, pic, src, description, isseries, needspayment, slug) VALUES ('677e9d14-4eef-4a4e-b3f2-86da543ad470', '{триллер,криминал}', 'США', '2002-01-01 03:00:00+03', 'Телефонная будка', 2002, '{"Джоэл Шумахер"}', '{"Ларри Коэн"}', '{63281e5e-84f7-4f57-843f-4201e8b2c3d2,0c43f299-4754-4069-8a59-38ee6713d3de,ae3cdb5b-cbe0-4927-b992-b90d6fb824be,52e9c0ec-92cf-4fdb-9824-15e648b35975,99d696f6-2bd9-490d-ba9f-6d6eeb801833,9c001d7e-89b8-469f-b252-ee3ef72ab03d}', '2002-01-01', 81, 'Ru', '???', 16, '{telefonnaia-budka.png}', '{telefonnaia-budka.mp4}', 'Один телефонный звонок может изменить всю жизнь человека или даже оборвать ее. Герой фильма Стью Шеферд становится пленником телефонной будки.
INSERT INTO films (id, genres, country, releaserus, title, year, director, authors, actors, release, duration, language, budget, age, pic, src, description, isseries, needspayment, slug) VALUES ('40f114e9-824f-4b6a-9a5e-80afa83833b0', '{триллер,боевик}', 'США', '2003-01-01 03:00:00+03', 'Рекрут', 2003, '{"Роджер Дональдсон"}', '{"Роджер Таун","Курт Уиммер","Митч Глейзер"}', '{a2a805ee-43c4-414f-9d7e-3593ec4f8967,ce2df4a2-4e10-4ad2-9a0b-9d079dba471a,6a8a1ffa-9d1b-41d5-9ca3-2cb5ed0010f9,06718cdc-5b7b-47de-8e1a-bc2ef2573b03,3840c600-5f00-4fb3-8b44-278c99474481,326c8540-38c4-466a-8a5f-8f8bd1eb2b8c}', '2003-01-01', 115, 'Ru', '???', 16, '{rekrut.png}', '{rekrut.mp4}', 'Джеймс Клэйтон - студент и опытный хакер. Он привлекает внимание спецслужб, и его вербуют в ЦРУ, упоминая таинственное исчезновение его отца в 90-х. Джеймс обучается у Уолтера Бёрка. Он отлично сдает все тесты, кроме последнего. Так он становится агентом без прикрытия и получает задание: найти «крота», который похищает опасный вирус из Лэнгли.', false, true, 'rekrut');
INSERT INTO films (id, genres, country, releaserus, title, year, director, authors, actors, release, duration, language, budget, age, pic, src, description, isseries, needspayment, slug) VALUES ('32391cc1-dda7-4e61-8d26-21ade150d023', '{криминал,комедия}', 'США', '2003-01-01 03:00:00+03', 'Разыскиваются в Малибу', 2003, '{"Джон Уайтселл"}', '{"Факс Бар","Адам Смолл","Джейми Кеннеди","Ник Свардсон"}', '{8bde1a9d-51b6-4f79-b1fe-a57dc0d01be8,b5dabb5c-e7b4-4b41-92b4-e363d032ee68,9427b251-a2ce-431a-827f-dbc7a359125b,aa9dc4cc-6310-4b3d-9908-1a1c26ee3527,33673c84-60dc-4018-9bf5-371c4159ac82,ec563802-5bee-4b18-bbbf-221301b62609}', '2003-01-01', 86, 'Ru', '???', 12, '{razyskivaiutsia-v-malibu.png}', '{razyskivaiutsia-v-malibu.mp4}', 'Не стоит ненавидеть его за то, кем он является. Или кем он не является. Все, чем хочет заниматься Брэд - или Би-рэд - разъезжать со своими дружками по Малибу и вести себя, как самый крутой черный рэппер в округе.
INSERT INTO films (id, genres, country, releaserus, title, year, director, authors, actors, release, duration, language, budget, age, pic, src, description, isseries, needspayment, slug) VALUES ('e4393244-8267-415f-8f43-650bf46a3d8b', '{комедия}', 'США', '2003-01-01 03:00:00+03', 'Тупой и еще тупее тупого: Когда Гарри встретил Ллойда', 2003, '{"Трой Миллер"}', '{"Питер Фаррелли","Беннетт Йеллин","Дэнни Байерс","Бобби Фаррелли","Роберт Бренер","Трой Миллер"}', '{48e25257-a36c-46f9-903e-7c963c202c96,f8c2fea6-16b4-4e6e-948f-c48ff61cb920,a003cd75-41a7-4375-935f-a78dcc34d199,88693477-8025-49dd-8b9b-31d2455638d1,85900bb7-86e9-4e93-bd25-4398b9b0347b,1c02032d-54c8-4787-ad16-a66347473f39}', '2003-01-01', 85, 'Ru', '???', 16, '{tupoi-i-eshche-tupee-tupogo:-kogda-garri-vstretil-lloida.png}', '{tupoi-i-eshche-tupee-tupogo:-kogda-garri-vstretil-lloida.mp4}', 'Как же встретились два героя-недоумка Гарри и Ллойд, известные по фильму «Тупой и еще тупее»? Оба несколько лет не ходили в школу, а учились на дому. Пришло время отправляться в школу, и прямо на улице Гарри и Ллойд столкнулись лбами...
INSERT INTO films (id, genres, country, releaserus, title, year, director, authors, actors, release, duration, language, budget, age, pic, src, description, isseries, needspayment, slug) VALUES ('6fc068ca-f4a3-4306-be51-b63fc770765f', '{триллер,драма,фантастика,боевик}', 'США', '2002-01-01 03:00:00+03', 'Эквилибриум', 2002, '{"Курт Уиммер"}', '{"Курт Уиммер"}', '{a674a888-7822-44a6-b788-4769542035f1,0a2a8a0d-5040-4490-a1fb-045cde91f5b3,ad3cf706-fffb-4a50-9ec9-4ea212b2e177,c44bbf5a-2b1f-4ef7-9e4a-4be8fe69cd50,4e779c57-6e6f-4d61-840b-d040d79c2dd3,c5acbd76-7e9f-40b2-a5b6-32cf3957f4eb}', '2002-01-01', 107, 'Ru', '???', 16, '{ekvilibrium.png}', '{ekvilibrium.mp4}', 'В будущем люди лишены возможности выражать эмоции. Это цена, которую человечество платит за устранение из своей жизни войны. Теперь книги, искусство и музыка находятся вне закона, а любое чувство — преступление, наказуемое смертью.
INSERT INTO films (id, genres, country, releaserus, title, year, director, authors, actors, release, duration, language, budget, age, pic, src, description, isseries, needspayment, slug) VALUES ('9d8a6964-8dbe-4d1a-98ac-31db8a0900fc', '{комедия,семейный}', 'США', '2003-01-01 03:00:00+03', 'Дежурный папа', 2003, '{"Стив Карр"}', '{"Джефф Родкей"}', '{82d078be-c32d-424c-bb57-26605c8ba8ab,1e245d53-ac74-4e7b-ac2a-7420a7b2e282,e7acbaf3-16da-45cf-8f98-e9eb6614e555,01c2caa8-1305-421c-a25d-43434011123d,4cbec207-46cd-4e0d-b21f-2459a594501c,5fd6388e-ed98-4ce0-9b1f-9a1a09eaf613}', '2003-01-01', 89, 'Ru', '???', 6, '{dezhurnyi-papa.png}', '{dezhurnyi-papa.mp4}', 'Чарли и Фила увольняют с работы в крупной корпорации. Теперь им самим придётся сидеть со своими сыновьями, так как оплачивать счета дорогостоящего детского центра уже не на что. Промучившись пару недель со своими отпрысками, папаши так увлекаются этим делом, что решают поставить дело на деловые рельсы и открывают новый центр дневного пребывания для детей.
INSERT INTO films (id, genres, country, releaserus, title, year, director, authors, actors, release, duration, language, budget, age, pic, src, description, isseries, needspayment, slug) VALUES ('c6b28541-1d9c-4c01-abd1-ea7a3690abb5', '{мелодрама,боевик,фэнтези,комедия}', 'США', '2003-01-01 03:00:00+03', 'Пуленепробиваемый', 2003, '{"Пол Хантер"}', '{"Этан Райфф","Сайрус Ворис"}', '{30081a19-1e15-4b6a-b685-90e9f9dc3bd9,357a538f-8e1c-404b-b89b-c38520bf9ae6,f763b036-497e-4a8b-94b5-1c238811c33f,10a4f09b-1a73-4ae6-ae28-923124c265a9,3e767bb8-2059-4ed3-88bd-6c1fddb27a50,0738a1d9-9192-400a-a243-fc7f73141862}', '2003-01-01', 104, 'Ru', '???', 12, '{puleneprobivaemyi.png}', '{puleneprobivaemyi.mp4}', 'Монах - мастер боевых искусств, который охраняет могущественный древний свиток - таинственный артефакт, содержащий ключ к безграничной власти. Монаху нужно найти следующего хранителя свитка, и поиски приводят его в Америку. Согласно древнему пророчеству и к изумлению Монаха его преемником оказывается обаятельный хулиган по имени Кар.
INSERT INTO films (id, genres, country, releaserus, title, year, director, authors, actors, release, duration, language, budget, age, pic, src, description, isseries, needspayment, slug) VALUES ('753de0d0-2f42-4763-8fff-9efe1b28990f', '{драма,приключения,фэнтези}', 'США', '2002-01-01 03:00:00+03', 'Властелин колец: Две крепости', 2002, '{"Питер Джексон"}', '{"Дж.Р.Р. Толкин","Фрэн Уолш","Филиппа Бойенс","Стивен Синклер","Питер Джексон"}', '{0640a0b4-da94-4462-a663-3f622f2d81f0,58b0991e-6b3c-4e81-9349-899a5e393b3b,85097183-8a82-406e-a5aa-798550ab7ab2,cf688a91-dea5-4565-adfa-a010b13a2395,5c70f6c9-1cce-4e9f-9aed-14c35e636444,9bd5246e-db0c-4270-ac85-3b5bf3a99d11}', '2002-01-01', 179, 'Ru', '???', 12, '{vlastelin-kolets:-dve-kreposti.png}', '{vlastelin-kolets:-dve-kreposti.mp4}', 'Братство распалось, но Кольцо Всевластья должно быть уничтожено. Фродо и Сэм вынуждены доверится Голлуму, который взялся провести их к вратам Мордора. Громадная армия Сарумана приближается: члены братства и их союзники готовы принять бой. Битва за Средиземье продолжается.', false, true, 'vlastelin-kolets:-dve-kreposti');
INSERT INTO films (id, genres, country, releaserus, title, year, director, authors, actors, release, duration, language, budget, age, pic, src, description, isseries, needspayment, slug) VALUES ('9253131c-ee40-4eee-9755-3a66a1c65d38', '{драма,мелодрама,комедия,спорт}', 'США', '2002-01-01 03:00:00+03', 'Играй, как Бекхэм', 2002, '{"Гуриндер Чадха"}', '{"Гуриндер Чадха","Гюльджит Биндра","Пол Маеда Берджес"}', '{bcecca19-6e19-4445-9f87-a2b1014cd96d,2af31360-53de-440a-89dd-d2a6b0443e7b,72ed0cac-444b-4133-97d4-0d2370efdbe5,6f7363bc-b13f-4d11-a90f-a2aa52c09acc,4beef32e-4a09-4551-9380-613eecd5ef9c,4c3e88bb-ab29-4cc9-8cac-f9970cf0baa2}', '2002-01-01', 112, 'Ru', '???', 16, '{"igrai,-kak-bekkhem.png"}', '{"igrai,-kak-bekkhem.mp4"}', 'Джесс только 18, но она твердо знает, что сделает ее счастливой: футбольная карьера, такая же, как у знаменитого игрока «Манчестер Юнайтед» Дэвида Бекхема. Пока она гоняет мяч в лондонском парке, доказывая соседским мальчишкам, что девушки играют в футбол не хуже, ее родители и многочисленные родственники, как и положено традиционной индийской семье, подыскивают для нее достойного мужа и строят планы о ее будущей карьере юриста.
INSERT INTO films (id, genres, country, releaserus, title, year, director, authors, actors, release, duration, language, budget, age, pic, src, description, isseries, needspayment, slug) VALUES ('19fa9c5a-e02f-49f8-a0b7-cc2dd6ecf4f5', '{фэнтези,комедия}', 'США', '2002-01-01 03:00:00+03', 'Цыпочка', 2002, '{"Том Брэди"}', '{"Том Брэди","Роб Шнайдер"}', '{4856eb01-5c2a-4423-b0cb-675054563fec,55e8019f-e709-4c36-9729-16692766fff6,d40b4465-6ac4-4b43-9cd0-db42f7138339,1b1abcbe-272e-4157-865f-cacd72680fce,5afa31a2-08ae-4a5f-8f8c-6d01c769efe5,12aa4cfa-710a-4320-b08b-cc65a9de5500}', '2002-01-01', 104, 'Ru', '???', 16, '{tsypochka.png}', '{tsypochka.mp4}', 'Популярная, но неприятная в общении старшеклассница Джессика однажды утром просыпается в теле 30-летнего мужчины с не самой привлекательной внешностью. Девушка отправляется на поиски своего тела, и это приключение помогает ей увидеть себя со стороны и понять, насколько поверхностной и недалёкой она была.', false, false, 'tsypochka');
INSERT INTO films (id, genres, country, releaserus, title, year, director, authors, actors, release, duration, language, budget, age, pic, src, description, isseries, needspayment, slug) VALUES ('91886496-873d-4c28-80ca-f389fa892807', '{мелодрама,комедия}', 'США', '2002-01-01 03:00:00+03', 'Любовь с уведомлением', 2002, '{"Марк Лоуренс"}', '{"Марк Лоуренс"}', '{19a8691c-ac00-4fde-9715-479a7f5f3470,7e27e3f2-695d-4ed5-8f1b-cde76684690b,bb477d1c-9f38-4b50-8d15-ba5e0fd26a4a,b6e589b4-018c-4bc7-9d37-454317e7cecb,4bf8183f-09fe-4e34-b08c-6e51210d732c,bdd97f65-87e8-4fef-9b82-6d2f049cdadf}', '2002-01-01', 101, 'Ru', '???', 16, '{liubov-s-uvedomleniem.png}', '{liubov-s-uvedomleniem.mp4}', 'Джордж Уэйд и шага не может сделать без Люси Келсон, работающей главным консультантом в его корпорации. Однако обращается он с ней скорее как с няней, а не как с блестящим юристом, окончившим Гарвард. По прошествии года все это надоедает Люси, и она решает уволиться. Джордж соглашается ее отпустить, но с одним условием — она должна найти себе достойную замену...', false, false, 'liubov-s-uvedomleniem');
INSERT INTO films (id, genres, country, releaserus, title, year, director, authors, actors, release, duration, language, budget, age, pic, src, description, isseries, needspayment, slug) VALUES ('b9bd5a49-8e58-4757-ad17-db4ab5b2765b', '{триллер,криминал,боевик}', 'США', '2003-01-01 03:00:00+03', 'Ограбление по-итальянски', 2003, '{"Ф. Гэри Грей"}', '{"Трой Кеннеди-Мартин","Донна Пауэрс","Уэйн Пауэрс"}', '{e24904b9-99cc-406f-8331-3c68d680403b,9c1906c8-ea27-4991-8f93-0ef17f64debd,82335932-a2b9-4502-8a7b-c192d735abe2,81b49fd5-b42b-4120-8474-43b08607c029,864d7708-28e0-49cc-830e-ce28eceaaa82,3377403d-d5bc-421a-858d-14ee26bf7624}', '2003-01-01', 111, 'Ru', '???', 12, '{ograblenie-po-italianski.png}', '{ograblenie-po-italianski.mp4}', 'Джон Бриджер всегда умел спланировать идеальное ограбление. Вместе со своей командой опытных бандитов он провернул не одно дело, но теперь решил уйти на покой. Впереди у Бриджера последнее задание: кража золотых слитков, в которой принимают участие инсайдер Стив, водитель Роб, взрыватель Левое ухо, технарь Лайл и Чарли - верный друг Бриджера и второй «планировщик» в их команде. Ограбление, изящное и быстрое, было разыграно как по нотам, но после его завершения веселье преступников было омрачено предательством...', false, true, 'ograblenie-po-italianski');
