INSERT INTO films (id, genres, country, releaserus, title, year, director, authors, actors, release, duration, language, budget, age, pic, src, description, isseries, needspayment, slug) VALUES ('9356a43d-177e-4f2f-b99a-35f47e1a0533', '{драма,криминал}', 'США', '1974-01-01 03:00:00+03', 'Крестный отец 2', 1974, '{"Фрэнсис Форд Коппола"}', '{"Фрэнсис Форд Коппола","Марио Пьюзо"}', '{82e6bb53-c159-4912-9ca1-c52d59ee1d3e,6ebd521e-5dd8-4452-963a-f73ca853cc2d,11480064-d121-46a2-af5d-eea0dc7303da,0cbee850-6301-442f-9694-0708780d7eff,9472d2e1-430f-4b54-b0a7-7b1e7f9870d5,ee0493a0-2134-4c8c-8a94-5cd928de0d3d}', '1974-01-01', 202, 'Ru', '???', 16, '{krestnyi-otets-2.png}', '{krestnyi-otets-2.mp4}', 'В центре драмы представители нового поколения гангстерского клана - дона Корлеоне и его сына, для которых не существует моральных преград на пути достижения поставленных целей. Они превращают мафию, построенную по патриархальным, еще сицилийским законам, в весьма прагматичную, жесткую корпорацию, плавно интегрирующуюся в большой бизнес Америки.', false, true, 'krestnyi-otets-2');
