INSERT INTO films (id, genres, country, releaserus, title, year, director, authors, actors, release, duration, language, budget, age, pic, src, description, isseries, needspayment, slug) VALUES ('c868d747-bf4e-4820-ab4b-521d4d2ad468', '{триллер,детектив,ужасы}', 'США', '1960-01-01 03:00:00+03', 'Психо', 1960, '{"Альфред Хичкок"}', '{"Джозеф Стефано","Роберт Блох"}', '{3b686bef-33c7-44e2-a591-65b716502b53,1c512cc0-7b94-48a9-a6a5-0e6de2a15296,3afdb7b7-26bf-4b14-a288-c20fa4ced1c4,045a452f-021b-432d-8371-225cb1de4797,63131a1b-2447-4be3-88cc-f1af145da51a,defc6c9e-8d56-4863-983b-88dccf54a6f5}', '1960-01-01', 109, 'Ru', '???', 16, '{psikho.png}', '{psikho.mp4}', 'Мэрион Крэйн недовольна романом с разведенным мужчиной, который уделяет ей внимание только наездами. Поэтому девушка крадет на работе крупную сумму и в спешке бежит из города. Мэрион останавливается на ночь в мотеле, которым управляет Норман Бейтс — юноша, страдающий от гнёта своей горячо любимой, но деспотичной матери. ', false, false, 'psikho');
INSERT INTO films (id, genres, country, releaserus, title, year, director, authors, actors, release, duration, language, budget, age, pic, src, description, isseries, needspayment, slug) VALUES ('64ff8075-a017-4100-b206-3536a11f23a4', '{триллер,драма,криминал,детектив,ужасы}', 'США', '1990-01-01 03:00:00+03', 'Молчание ягнят', 1990, '{"Джонатан Демме"}', '{"Томас Харрис","Тед Тэлли"}', '{608aa66b-e833-426e-b81b-d76d21df67a1,7187e863-3969-498c-a0b2-6d83142e019c,137612d7-0248-4ba9-b9b0-5f265c8e5249,a4394d9b-38c9-4e12-97b3-e6cf0dcaf9e1,20bce0ac-3444-49c3-b647-5b3fc34bca60,5635434d-7c63-46a5-afcb-f8627e3e8d06}', '1990-01-01', 114, 'Ru', '???', 18, '{molchanie-iagniat.png}', '{molchanie-iagniat.mp4}', 'Психопат похищает и убивает молодых женщин по всему Среднему Западу. ФБР, уверенное, что все преступления совершены одним и тем же человеком, поручает агенту Клариссе Старлинг встретиться с заключенным-маньяком Ганнибалом Лектером, который мог бы помочь составить психологический портрет убийцы. Сам Лектер отбывает наказание за убийства и каннибализм. Он согласен помочь Клариссе лишь в том случае, если она попотчует его больное воображение подробностями своей личной жизни.', false, true, 'molchanie-iagniat');
INSERT INTO films (id, genres, country, releaserus, title, year, director, authors, actors, release, duration, language, budget, age, pic, src, description, isseries, needspayment, slug) VALUES ('9e6dc5b3-d8f5-4df6-ad1c-47fbfb7105fd', '{драма,криминал,детектив}', 'США', '1956-01-01 03:00:00+03', '12 разгневанных мужчин', 1956, '{"Сидни Люмет"}', '{"Реджинальд Роуз"}', '{8293222f-1465-4266-9aed-93fb44b1c8a3,6b13b63f-18d1-45e8-b7b4-1bd06e9caea0,1236a79b-2efa-4472-883d-c05dbe271cb3,d0391ad8-9518-4162-bb2c-3781c65d4e1f,76f0f9e2-c89b-4139-a0f7-858514933ad7,0a4d9fca-0b96-4ccc-ae63-498e768f1f75}', '1956-01-01', 96, 'Ru', '???', 16, '{12-razgnevannykh-muzhchin.png}', '{12-razgnevannykh-muzhchin.mp4}', 'Юношу обвиняют в убийстве собственного отца, ему грозит электрический стул. Двенадцать присяжных собираются, чтобы вынести вердикт: виновен или нет. С начала заседания почти все считают подсудимого виновным, и лишь только один из двенадцати позволил себе усомниться. И он решает убедить остальных в своей правоте.', false, false, '12-razgnevannykh-muzhchin');
